b:if.script:
  //
  (function($){
    $.fn.relatedPosts=function(config){
      var me = this, blogId, postId;

      var settings = $.extend(true,{            
        url: '',
        postId:'',
        labels:[],
        title:'Related Posts: ',
        itemClass: 'panel-body',
        //wrapperClass: 'panel-body1',
        titleClass: 'title',
        shuffle:true,
        thumbUsePost:true,
        thumbWidth:64,
        thumbHeight:64,
        maxPosts: 3
      },config);

      if(!$.isArray(settings.labels)){
        settings.labels = settings.labels.trim().split(',');
      }
      return init();

      function init(){
        $.ajax({
          url: '/feeds/posts/default/-/'+settings.labels+'?alt=json&max-results='+settings.maxPosts,      
          type: 'get',
          dataType: "jsonp",
          cache:true,
          success: function(data){
            // get the blog feed id
            blogId = data.feed.id.$t;
            if(settings.postId !== ''){
              // get the current post id
              postId = blogId + '.post-' + settings.postId;
            }
            // get related posts
            var posts = findRelatedPosts(data.feed.entry);

            var pc = posts.length;

            if(pc>0) {
              // add section title  
              me.addClass("panel panel-default");
              me.append('<div class="panel-heading"><h3 class="panel-title">'+settings.title+'</h3></div>');

              // construct the output
              for (var i = 0; i < pc; i++){                
                var entry = posts[i];
                var href = getLink(entry);
                // the link
                var link = $('<a/>',{'href':href});
                // the thumbnail
                var thumb = findThumbnail(entry,entry.title.$t);

                var content = getContent(entry.content.$t);

                // the item block
                var div = $('<div/>',{'class':settings.itemClass});       
                div.html('<div class="media"><div class="media-left"></div><div class="media-right"><h4 id="media-heading" class="media-heading"></div></div></div>');
                // add thumbnail
                div.find(".media-left").append(thumb);
                // wrap the link
                thumb.wrap(link)
                // add post title
                div.find("h4").append(link.text(entry.title.$t));
                //pTitle.append();
                // add the item block
                div.find(".media-right").append(content);
                me.append(div);
                div.click(function() { window.location = href; })
              }
            }
          }
        });   
      }

      function getContent(con){ var content = $(con).text().substring(0,155);return (content)?content + " ..." :"No description available!!";}
      // gets the entry link
      function getLink(entry){
        var href = '#';
        $.each(entry.link, function(i, v){
          if(v.rel=='alternate'){
            href = v.href;
            return false;
          };
        });
        return href;
      }

      // find related entries
      function findRelatedPosts(entries){
        var posts = [];
        var cats = settings.labels;

        for (var i = 0; i < entries.length; i++){
          if(!matchCurrentPost(entries[i])){    
            if(matchCategory(entries[i],cats)){
              posts.push(entries[i]);
            }
          }
        }

        // shuffle array
        if(settings.shuffle){
          posts.sort(function() { return 0.5 - Math.random(); });
        }
        return posts.slice(0,settings.maxPosts);
      }

      function findThumbnail(entry, title){
        if(settings.thumbUsePost){
          if(entry.content){
            var div = $('<div/>').html(entry.content.$t);
            var imgs = div.find('img');
            if(imgs.length > 0){     
              return $('<img/>',{'src':imgs[0].src, 'alt': imgs[0].alt, 'title': title, 'width':settings.thumbWidth,'height':settings.thumbHeight});
            }
          }
        }else{
          return $('<img/>',{'src':entry.media$thumbnail.url,'title': title,'alt':title,'width':entry.media$thumbnail.width,'height':entry.media$thumbnail.height});
        }
      }

      function matchCurrentPost(entry){
        var match = false;
        if(postId){
          var id = entry.id.$t;
          match = (id == postId);    
        }else{
          var href = location.href.replace(location.hash,'');
          href = href.split('?')[0].toLowerCase();            
          $.each(entry.link, function(i,v){
            if(v.rel == 'alternate'){                
              if(v.href.toLowerCase().replace(/https?:\/\/[^\/]+/i, "") == href.replace(/https?:\/\/[^\/]+/i, "")){                  
                match = true;                  
              }
            }
          });
        }          
        return match;
      }

      // find any matched category
      function matchCategory(entry, tags){
        if(entry.category){
          for(var i = 0; i < entry.category.length; i++){
            var tag = entry.category[i].term;
            if($.inArray(tag,tags) >= 0){
              return true;
            }
          }    
        }
        return false;
      }
    };
  })(jQuery);

  // related posts
  if (!(typeof labels === 'undefined') && (typeof labels === 'object')) {   
    if(labels.length > 0) {
      labels = labels.shift();        
      $('#related_posts').relatedPosts({labels:labels,title:'Related Post',url:"http://sandbox-mahavir-munot.blogspot.in/"});
    }
  }
